// Parse HTML + RSS + API sources into unified job list + tokens (for digest)
function getBody(nodeName){
  const n=$items(nodeName,0,0)[0]?.json;
  if(!n) return '';
  if(typeof n==='string') return n;
  if(typeof n.body==='string') return n.body;
  if(typeof n.data==='string') return n.data;
  return n.body||'';
}
function getJson(nodeName){
  const n=$items(nodeName,0,0)[0]?.json;
  return n||null;
}
const htmlSources=[
  { key: 'web3.career', body: getBody('Fetch Web3.career'), base: 'https://web3.career' },
  { key: 'cryptocurrencyjobs.co', body: getBody('Fetch CryptocurrencyJobs'), base: 'https://cryptocurrencyjobs.co' },
  { key: 'cryptojobslist.com', body: getBody('Fetch CryptoJobsList'), base: 'https://cryptojobslist.com' },
  { key: 'remote3.co', body: getBody('Fetch Remote3'), base: 'https://remote3.co' }
];
const rssSources=[
  { key: 'cryptocurrencyjobs.co', body: getBody('Fetch CryptocurrencyJobs RSS') },
  { key: 'cryptojobslist.com', body: getBody('Fetch CryptoJobsList RSS') }
];
function stripTags(html){return (html||'').toString().replace(/<[^>]*>/g,' ').replace(/\s+/g,' ').trim();}
function toAbsolute(href, base){if(!href) return null; if(href.startsWith('http')) return href; if(href.startsWith('//')) return 'https:'+href; if(href.startsWith('/')) return base+href; return base+'/'+href;}
function extractJobsFromHtml(html, domain, base){
  const results=[];
  const re=/<a[^>]+href=\"([^\"]+)\"[^>]*>([\s\S]*?)<\/a>/gi;
  const blacklist=['about','blog','privacy','terms','newsletter','companies','salaries','subscribe','advertise'];
  let m;
  while((m=re.exec(html))!==null){
    const href=toAbsolute(m[1], base); if(!href) continue;
    const lower=href.toLowerCase();
    if(!lower.includes(domain)) continue;
    if(blacklist.some(b=>lower.includes('/'+b))) continue;
    if(domain==='web3.career' && !/(remote|web3|blockchain|crypto|solidity|rust|engineer|developer)/.test(lower)) continue;
    const title=stripTags(m[2]).slice(0,140);
    if(!title||title.length<5) continue;
    results.push({ title, url: href, source: domain });
  }
  const seen=new Set();
  return results.filter(r=>{ if(seen.has(r.url)) return false; seen.add(r.url); return true; });
}
function extractJobsFromRss(xml, source){
  const results=[];
  const items=xml.match(/<item[\s\S]*?<\/item>/gi)||[];
  for(const it of items){
    const t=(it.match(/<title>([\s\S]*?)<\/title>/i)||[])[1]||'';
    const l=(it.match(/<link>(https?:\/\/[^<]+)<\/link>/i)||[])[1]||'';
    const title=stripTags(t).slice(0,140);
    if(title && l){ results.push({ title, url: l, source }); }
  }
  return results;
}
let jobs=[];
for(const s of htmlSources){ if(s.body){ jobs=jobs.concat(extractJobsFromHtml(s.body, s.key, s.base)); } }
for(const s of rssSources){ if(s.body){ jobs=jobs.concat(extractJobsFromRss(s.body, s.key)); } }
const rok=getJson('Fetch RemoteOK API');
if(Array.isArray(rok)){
  for(const j of rok){
    if(!j || !j.url || !j.position) continue;
    const title=stripTags(j.position).slice(0,140);
    const tags=(j.tags||[]).map(x=>String(x).toLowerCase());
    const lower=title.toLowerCase();
    const match=['crypto','blockchain','web3','solidity','rust','defi','nft','smart'].some(k=>lower.includes(k)||tags.includes(k));
    if(match){ jobs.push({ title, url: j.url, source: 'remoteok.com' }); }
  }
}
