let cmcTokens=[];
if(cmc&&cmc.data&&Array.isArray(cmc.data)){
  cmcTokens=cmc.data.slice(0,20).map(c=>({name:c.name,symbol:c.symbol,slug:c.slug,date_added:c.date_added,url:`https://coinmarketcap.com/currencies/${c.slug}/`}));
}
const cgNode=$items('Fetch CoinGecko New',0,0)[0]?.json;
let cgArr=[];
if(Array.isArray(cgNode)) cgArr=cgNode; else if(cgNode && typeof cgNode.body==='string'){ try{ const parsed=JSON.parse(cgNode.body); if(Array.isArray(parsed)) cgArr=parsed; }catch(e){} }
let cgTokens=[];
if(Array.isArray(cgArr)){
  cgTokens=cgArr.slice(0,20).map(x=>({name:x.name,symbol:x.symbol,slug:x.id,url:`https://www.coingecko.com/coins/${x.id}`}));
}
const skills=['solidity','rust','smart contract','blockchain','web3','defi','nft','react','node.js','next.js','telegram','discord','ton','solana'];
const filteredJobs=jobs.filter(j=>{const t=j.title.toLowerCase(); return skills.some(s=>t.includes(s));}).slice(0,25);
return [{ json: { jobs: filteredJobs, tokens: [...cmcTokens, ...cgTokens] } }];

